using UnityEngine;using System.Collections;public class AnimationControl : MonoBehaviour {  public Animator animatorController;    // Use this for initialization  void Start () {    if(animatorController == null) {      animatorController = GetComponentInChildren<Animator>();    }	}		void Update () {    float horizontal = Input.GetAxis("Horizontal");    float vertical = Input.GetAxis("Vertical");    animatorController.SetFloat("horizontal", horizontal);    animatorController.SetFloat("vertical", vertical);	}}